import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom";
import axios from "axios";

import './Search.scss';


function Search() {
  const [products, setProduct] = useState([]);
  const [loading, setLoading] = useState(false);
  const [search, setSearch] = useState("");
  const [filteredProducts, setFilteredProducts] = useState([]);

  useEffect(() => {
    setLoading(true);
    axios
      .get("https://5e9935925eabe7001681c856.mockapi.io/api/v1/catalog")
      .then(res => {
        setProduct(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.log(err);
      });
  }, []);

  useEffect(() => {
    setFilteredProducts(
      products.filter(product =>
        product.name.toLowerCase().includes(search.toLowerCase())
      )
    );
  }, [search, products]);

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <aside className= "search__overlay">
      <div className= "search__sidebar" >
        <div className= "search__header">
          <button className= "search__close" type= "button" name= "search__close">
            X
          </button>
            <span> Busca <input type="text" className="content__search"/>
            </span>
        </div>
        <div className= "search__content">
            <ul className= "mincart__content-full">

            </ul>
            <div className= "search__content-empty">
                <span>
                    Seu carrinho está vazio :( 
                </span>
            </div>
        </div>
        <div className= "search__footer">
            <div className= "search__footer__button">
                <span className= "search__footer-full">
                    Subtotal R$
                </span>
                <a>
                <span className="search__footer-empty">
                    Continuar comprando
                </span>
                </a>
            </div>
        </div>
    </div>
</aside>


    // <div className="App">
    //   <h1>Busca</h1>
    //   <input
    //     type="text"
    //     placeholder=" "
    //     onChange={e => setSearch(e.target.value)}
    //   />
    //   {filteredProducts.map((product, idx) => (
    //     <ProductDetail key={idx} {...product} />
    //   ))}
    // </div>
  );
}



const ProductDetail = props => {
  const { name, image, actual_price } = props;
  return (
    <div className="search">
      <p className="product__name">
        <img src={image} alt={name} style={{ width: "40px", height: "40px" }} />
      </p>
      <p className="product__price">{actual_price}</p>
      <hr/>
    </div>

  );
};

const rootElement = document.getElementById("root");
ReactDOM.render(<Search />, rootElement);


export default Search;
